mapper.update:
update activity_information set activity_infomation_join = activity_infomation_join+#{num} ,set
activity_information_surplus_number = activity_information_surplus_number-#{num}

    activity_information_id = #{activityId}

mapper.select:
select activity_information_id id, activity_information_name `name`, activity_information_describe `desc`, user_name releaser, activity_information_release_time releaseTime, activity_information_start_time startTime, activity_infomation_join_time joinTime, activity_information_surplus_number surNumber, activity_information_limit `limit`, activity_infomation_join `join`, activity_infomation_status state
from activity_information left join `user` on activity_information_releaser_id = user_id
and activity_information_name = #{name}
and activity_infomation_status = #{status}
order by activity_information_release_time desc limit #{pageIndex},#{pageSize}

select
count(1) from activity_information


        and activity_information_name = #{name}


        and activity_infomation_status = #{status}


order by activity_information_release_time desc
